import { Component, OnInit } from '@angular/core';
import { LeadService } from '../../services/lead.service';
import { Lead } from '../../models/lead.model';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent implements OnInit {
  leadCount = 0;
  qualifiedLeadCount = 0;
  averageScore = 0;
  isLoading = true;

  // Make Math available to the template
  Math = Math;

  // Placeholder data for charts
  qualificationData = {
    labels: ['High (80-100)', 'Medium (60-79)', 'Low (0-59)'],
    datasets: [
      {
        data: [0, 0, 0],
        backgroundColor: ['#34D399', '#4A6BFF', '#F59E0B']
      }
    ]
  };

  industryData: {
    labels: string[];
    datasets: Array<{
      data: number[];
      backgroundColor: string[];
    }>;
  } = {
    labels: [],
    datasets: [
      {
        data: [],
        backgroundColor: []
      }
    ]
  };

  constructor(private leadService: LeadService) { }

  ngOnInit(): void {
    this.loadDashboardData();
  }

  loadDashboardData(): void {
    this.isLoading = true;

    this.leadService.getAllLeads().subscribe({
      next: (response) => {
        if (response.success && response.data) {
          const leads = response.data as Lead[];
          this.leadCount = leads.length;

          // Calculate qualified leads (score >= 70)
          const qualifiedLeads = leads.filter((lead: Lead) => {
            const scoreStr = lead.qualificationScore !== undefined ? String(lead.qualificationScore) : '0';
            const score = parseInt(scoreStr, 10);
            return score >= 70;
          });
          this.qualifiedLeadCount = qualifiedLeads.length;

          // Calculate average score
          if (leads.length > 0) {
            const totalScore = leads.reduce((sum: number, lead: Lead) => {
              const scoreStr = lead.qualificationScore !== undefined ? String(lead.qualificationScore) : '0';
              const score = parseInt(scoreStr, 10);
              return sum + score;
            }, 0);
            this.averageScore = Math.round(totalScore / leads.length);
          }

          // Process qualification score distribution
          const highScoreLeads = leads.filter((lead: Lead) => {
            const scoreStr = lead.qualificationScore !== undefined ? String(lead.qualificationScore) : '0';
            const score = parseInt(scoreStr, 10);
            return score >= 80;
          }).length;

          const mediumScoreLeads = leads.filter((lead: Lead) => {
            const scoreStr = lead.qualificationScore !== undefined ? String(lead.qualificationScore) : '0';
            const score = parseInt(scoreStr, 10);
            return score >= 60 && score < 80;
          }).length;

          const lowScoreLeads = leads.filter((lead: Lead) => {
            const scoreStr = lead.qualificationScore !== undefined ? String(lead.qualificationScore) : '0';
            const score = parseInt(scoreStr, 10);
            return score < 60;
          }).length;

          this.qualificationData.datasets[0].data = [highScoreLeads, mediumScoreLeads, lowScoreLeads];

          // Process industry distribution
          const industries: Record<string, number> = {};
          leads.forEach((lead: Lead) => {
            const industry = lead.industry || 'Unknown';
            industries[industry] = (industries[industry] || 0) + 1;
          });

          const industryLabels = Object.keys(industries);
          const industryValues = Object.values(industries);
          const industryColors = this.generateColors(industryLabels.length);

          this.industryData.labels = industryLabels;
          this.industryData.datasets[0].data = industryValues;
          this.industryData.datasets[0].backgroundColor = industryColors;
        }

        this.isLoading = false;
      },
      error: (error) => {
        console.error('Error loading dashboard data:', error);
        this.isLoading = false;
      }
    });
  }

  // Helper method to generate colors for charts
  generateColors(count: number): string[] {
    const baseColors = ['#4A6BFF', '#34D399', '#F59E0B', '#EF4444', '#8B5CF6', '#EC4899', '#10B981', '#6366F1'];
    const colors = [];

    for (let i = 0; i < count; i++) {
      colors.push(baseColors[i % baseColors.length]);
    }

    return colors;
  }
}
<div class="dashboard-container">
  <div class="container">
    <h1 class="dashboard-title">Dashboard</h1>
    <p class="dashboard-subtitle">Overview of your lead generation performance</p>

    <div *ngIf="isLoading" class="loading-indicator">
      <div class="spinner"></div>
      <p>Loading dashboard data...</p>
    </div>

    <div *ngIf="!isLoading" class="dashboard-content">
      <!-- Stats Cards -->
      <div class="stats-grid">
        <div class="stat-card">
          <div class="stat-icon">
            <span class="material-icons">people</span>
          </div>
          <div class="stat-content">
            <h3>Total Leads</h3>
            <p class="stat-value">{{ leadCount }}</p>
          </div>
        </div>

        <div class="stat-card">
          <div class="stat-icon qualified">
            <span class="material-icons">verified</span>
          </div>
          <div class="stat-content">
            <h3>Qualified Leads</h3>
            <p class="stat-value">{{ qualifiedLeadCount }}</p>
          </div>
        </div>

        <div class="stat-card">
          <div class="stat-icon score">
            <span class="material-icons">analytics</span>
          </div>
          <div class="stat-content">
            <h3>Average Score</h3>
            <p class="stat-value">{{ averageScore }}<span class="stat-unit">/100</span></p>
          </div>
        </div>
      </div>

      <!-- Charts Section -->
      <div class="charts-section">
        <div class="row">
          <div class="col-12 col-md-6">
            <div class="chart-card">
              <h3 class="chart-title">Lead Qualification Distribution</h3>
              <div class="chart-placeholder">
                <!-- Chart.js would be rendered here -->
                <div class="placeholder-chart pie-chart">
                  <div *ngFor="let value of qualificationData.datasets[0].data; let i = index" 
                       class="placeholder-segment" 
                       [style.background-color]="qualificationData.datasets[0].backgroundColor[i]"
                       [style.width.%]="value > 0 ? (value / leadCount) * 100 : 0">
                  </div>
                </div>
                <div class="chart-legend">
                  <div *ngFor="let label of qualificationData.labels; let i = index" class="legend-item">
                    <span class="legend-color" [style.background-color]="qualificationData.datasets[0].backgroundColor[i]"></span>
                    <span class="legend-label">{{ label }}: {{ qualificationData.datasets[0].data[i] }}</span>
                  </div>
                </div>
              </div>
            </div>
          </div>

          <div class="col-12 col-md-6">
            <div class="chart-card">
              <h3 class="chart-title">Leads by Industry</h3>
              <div class="chart-placeholder">
                <!-- Chart.js would be rendered here -->
                <div class="placeholder-chart bar-chart">
                  <div *ngFor="let value of industryData.datasets[0].data; let i = index" 
                       class="placeholder-bar" 
                       [style.backgroundColor]="industryData.datasets[0].backgroundColor[i]"
                       [style.height.%]="value > 0 && industryData.datasets[0].data.length > 0 ? (value / Math.max.apply(null, industryData.datasets[0].data)) * 80 : 0">
                  </div>
                </div>
                <div class="chart-legend">
                  <div *ngFor="let label of industryData.labels; let i = index" class="legend-item">
                    <span class="legend-color" [style.background-color]="industryData.datasets[0].backgroundColor[i]"></span>
                    <span class="legend-label">{{ label }}: {{ industryData.datasets[0].data[i] }}</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <!-- Actions Section -->
      <div class="actions-section">
        <div class="card">
          <h3 class="section-title">Quick Actions</h3>
          <div class="action-buttons">
            <a routerLink="/add-lead" class="btn btn-primary">
              <span class="material-icons">add</span>
              Add New Lead
            </a>
            <a routerLink="/leads" class="btn btn-outline">
              <span class="material-icons">list</span>
              View All Leads
            </a>
            <button class="btn btn-outline" (click)="loadDashboardData()">
              <span class="material-icons">refresh</span>
              Refresh Data
            </button>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
